import base64

# Assume 'encoded_html' is the Base64 encoded HTML string you received
encoded_html = ""
decoded_bytes = base64.b64decode(encoded_html)

# Convert bytes to string (assuming the original encoding was UTF-8)
html_content = decoded_bytes.decode("utf-8")

print(html_content)

file_path = "test/html/html_test.html"

# Open the file in write mode and write the HTML content
with open(file_path, "w", encoding="utf-8") as file:
    file.write(html_content)
    print(f"HTML content written to {file_path}")
